SELECT DISTINCT 'oxartextends' AS `object`, oxartextends.OXID AS `object_id`,'oxlongdesc_1' AS `field`, oxarticles.OXSHOPID AS `shop_id`, 0 AS `lang_id`, 'translate' AS `mode`, 'pending' AS `status` FROM oxartextends LEFT JOIN oxarticles ON (oxartextends.OXID = oxarticles.OXID) WHERE (oxarticles.OXPARENTID = '') AND (LENGTH(oxartextends.OXLONGDESC_1) < 250) ORDER BY oxarticles.OXTIMESTAMP DESC;