SELECT DISTINCT 'oxarticles' AS `object`, oxarticles.OXID AS `object_id`, 'oxsearchkeys' AS `field`, oxarticles.OXSHOPID AS `shop_id`, 0 AS `lang_id`, 'create' AS `mode`, 'pending' AS `status` FROM oxarticles WHERE (oxarticles.OXPARENTID = '') AND (LENGTH(oxarticles.OXSEARCHKEYS) < 50) ORDER BY oxarticles.OXTIMESTAMP DESC;